import React, {useRef} from 'react';
import './App.scss';

//import canvas from react three fiber
import { Canvas, useFrame } from 'react-three-fiber';
//import { Box } from '@react-three/drei';


const SpinningMesh = ({position, color, args}) => {
  const mesh = useRef(null)
  useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01));

  return (
    <mesh castShadow position={position} ref={mesh}>
      <boxBufferGeometry attach='geometry' args={args}/>
      <meshStandardMaterial attach='material' color={color}/>
    </mesh>
  )
}





function App() {

  return ( 
  <>
  <Canvas 
  shadows 
  colorManagement 
  camera={{position: [-5, 2, 10], fov: 60}}>

    <ambientLight intensity={0.3} />
    <pointLight intensity={0.5} position={[-10, 0, -20]}/>
    <pointLight intensity={1.5} position={[0, -10, 0]}/>
    <directionalLight 
      castShadow
      intensity={1.5} 
      position={[0,5,0]} 
      shadow-mapSize-width={1024} 
      shadow-mapSize-height={1024} 
      shadow-camera-far={50}
      shadow-camera-left={-10}
      shadow-camera-rigth={10}
      shadow-camera-top={10}
      shadow-camera-bottom={-10}
    />

    <group>
      <mesh 
        receiveShadow 
        rotation={[-Math.PI / 2,0,0]} 
        position={[0,-3,0]}>
        <planeBufferGeometry attach='geometry' args={[100,100]}/>
      <shadowMaterial attach='material' opacity={0.4} />
      </mesh>
    </group>

    <SpinningMesh position={[0,1,0]} color={"lightblue"} args={[3,2,1]}/>
    <SpinningMesh position={[-2,1,-5]} color={"pink"} />
    <SpinningMesh position={[5,1,-2]} color={"pink"}/>
  </Canvas>
  </>);
}

export default App;
