import React, {useRef, useState} from 'react';
import './App.scss';

//import canvas from react three fiber
import { Canvas, useFrame } from 'react-three-fiber';
import { softShadows, MeshWobbleMaterial, OrbitControls } from '@react-three/drei';
import {useSpring, a} from '@react-spring/three'
import Bunny from "./Bunny.jsx"

softShadows();

const SpinningMesh = ({position, color, args, speed}) => {
  const mesh = useRef(null)
  useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01));
//
const [expand, setExpand] = useState(false);

const props = useSpring({
  scale: expand ? [1.4, 1.4, 1.4]:[1,1,1],
});


  return (
    <a.mesh onClick={() => setExpand(!expand)}
    scale={props.scale} 
    castShadow position={position} ref={mesh}>
      <boxBufferGeometry attach='geometry' args={args}/>
      <MeshWobbleMaterial speed={speed} factor={0.6} attach='material' color={color}/>
      {/*<meshStandardMaterial attach='material' color={color}/>*/}
    </a.mesh>
  )
}





function App() {

  return ( 
  <>
  <Canvas 
  shadows 
  colorManagement 
  camera={{position: [-5, 2, 10], fov: 60}}>

    <ambientLight intensity={0.3} />
    <pointLight intensity={0.5} position={[-10, 0, -20]}/>
    <pointLight intensity={1.5} position={[0, -10, 0]}/>
    <directionalLight 
      castShadow
      intensity={1.5} 
      position={[0,10,0]} 
      shadow-mapSize-width={1024} 
      shadow-mapSize-height={1024}
      shadow-camera-far={50}
      shadow-camera-near={0}
      shadow-camera-left={-20}
      shadow-camera-rigth={20}
      shadow-camera-top={20}
      shadow-camera-bottom={-20}
    />
<Bunny/>
{/**
    <group>

      <mesh 
        receiveShadow 
        rotation={[-Math.PI /2,0,0]} 
        position={[0,-3, 0]}>

        <planeBufferGeometry attach='geometry' args={[100,100]}/>
        <meshStandardMaterial attach='material' opacity={0.3} />
      </mesh>

      <SpinningMesh position={[0,1,0]} color={"lightblue"} args={[3,2,1]} speed={1}/>
      <SpinningMesh position={[-2,1,-5]} color={"pink"}  speed={2}/>
      <SpinningMesh position={[5,1,-2]} color={"pink"} speed={2}/>
    <Bunny/>
    </group>
 */}
    <OrbitControls />
  </Canvas>
  </>);
}

export default App;


